import React from 'react';
import {Link} from 'react-router-dom';


import s from '../../styles/Products.module.css';

const Products = ({title, products = [], amount}) => {
    console.log(products)

    const list = products.filter(_, index => index < amount);

    return (
        // 1.01.04
        <section className={s.products}>
            {title && <h2>{title}</h2>}

            <div className={s.list}>
                {products.map(
                    ({id, image, title, category: {name: cat}, price}) => (
                        <Link to={`/products/${id}`} key={id} className={s.product}>
                            <div className={s.image} style={{backgroundImage: `url(${image})`}}/>

                         <div className={s.wrapper}>
                             <h3 className={s.title}>{title}</h3>
                             <div className={s.cat}>{cat}</div>
                             <div className={s.info}>
                                 <div className={s.prices}>
                                     <div className={s.price}>{price} $</div>
                                     <div className={s.oldPrice}>{Math.floor(price * 0.5)}$</div>
                                 </div>
                             </div>

                             <div className={s.purchases}>
                                 {Math.floor(Math.random() * 20 + 1)} purchases
                             </div>
                         </div>



                        </Link>
                    ))}
            </div>

        </section>
    );
};

export default Products;
