import React, { useEffect, useState } from 'react';
import Poster from "../Poster/Poster";
import Products from "../Products/Products";
import {useSelector} from "react-redux";

const Home = () => {

    const {list}=useSelector(({products})=>products)
    // console.log(list)

    const [amount, setAmount] = useState(5);

    useEffect(() => {
        const handleResize = () => {
            // Определите условия, при которых вы хотите изменить значение amount
            if (window.innerWidth < 768) {
                setAmount(3);
            } else {
                setAmount(5);
            }
            console.log(amount);
        };

        // Добавьте обработчик события изменения размера окна
        window.addEventListener('resize', handleResize);

        // Вызовите обработчик события при монтировании компонента
        handleResize();

        // Удалите обработчик события при размонтировании компонента
        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);



    return (
        <>
            <Poster/>
            <Products products={list} amount={amount}/>
        </>
    );
};

export default Home;
